def number_of_handles(journalists)
  puts "There are #{journalists.length} handles of real live journalists in this array!"
end

# 2--- Quel est le handle le plus court de cette liste ? ---
def shortest_handle(journalists)
  puts "#{journalists.min_by(&:length)} has the shortest handle of all!"
end

# 3--- Combien y-a-t'il de handle contenant 5 caractères ? (le @ ne compte pas pour un caractère) ---
def handles_with_5_chars(journalists)
  five_letter_handles = []
  journalists.each do |n|
    if n.size == 6
      five_letter_handles.push(n)
    end
  end
  puts "There are #{five_letter_handles.count} handles with 5 characters (not counting the @ of course!)."
end

# 4--- Combien commencent par une majuscule (première lettre juste après le @) ? ---
def cap_handles(journalists)
  first_letter = []             # Create array to store 2nd character of each string in journalists array
  journalists.each do |n|
    letter = n[1,1]
    first_letter.push(letter)
  end
  starts_with_cap = []          # Create array to store only those that start with capital letter
  first_letter.each do |n|
    if n == n.upcase and n != "_"
    starts_with_cap.push(n)
    end
  end
  puts "#{starts_with_cap.count} handles start with a capital letter"    # Count the number of items in the array storing only capital letters
end

# 5--- Trie la liste de handle par ordre alphabétique. ---
def alph_handles(journalists)
  in_alph_order = journalists.map(&:downcase)
  puts "Here are the handles in alphabetical order: #{in_alph_order.sort}"
end

# 6--- Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après) ---
def handle_length(journalists)
  by_length = journalists.sort_by(&:length)
  puts "Here are the handles from shortest to longest: #{by_length}"
end

# 7--- Quelle est la position dans l'array de la personne @epenser ? ---
def find_epenser(journalists)
  puts "@epenser is number #{journalists.index("@epenser")} on the list."
end

# 8---Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc) ---
def handles_by_size(journalists)
  number_handles = []                               # Empty array to store handles with specific number of characters
  i = 2                                             # Since "@" doesn't count as a character, start count at 2 instead of 1
  while i < journalists.max_by(&:length).size + 1   # Only iterate until we reach the longest handle in array
    journalists.each do |n|                         # Add handle to empty array if it matches number of characters
      if n.size == i
        number_handles.push(n)
      end
    end
    puts "There are #{number_handles.count} handles with #{i - 1} characters" # Show how many handles there are for each number of characters (-1 since @ doesn't count as a character)
    number_handles = []                                                       # Clear out array and repeat with next number
    i = i + 1
  end
end


def menu_options(journalists)
  user_input = ""
  while user_input != "q"
    p "#" * 50
    puts "
          Hello! What do you want to know about this array of journalists' Twitter handles?
          Press 1: How many handles are in this array?
          Press 2: What is the shortest handle in the list?
          Press 3: How many handles contain 5 characters?
          Press 4: How many handles start with a capital letter?
          Press 5: Sort the handles alphabetically
          Press 6: Sort the handles by length
          Press 7: What number is @epenser on the list?
          Press 8: Give me a breakdown of handles by length
          Press q: Exit program
        "
        p "#" * 50
        user_input = gets.chomp
        case user_input
        when "1"
          number_of_handles(journalists)
        when "2"
          shortest_handle(journalists)
        when "3"
          handles_with_5_chars(journalists)
        when "4"
          cap_handles(journalists)
        when "5"
          alph_handles(journalists)
        when "6"
          handle_length(journalists)
        when "7"
          find_epenser(journalists)
        when "8"
          handles_by_size(journalists)
        when "q"
          puts "Thanks for using this application!"
        else 
          puts "Please press a number 1-8"
      end
    end  
  end

menu_options(journalists)